using namespace std;

string removeComments(string input_string)
{
    string output_string = "";
    int flag = 0;

    for (int i = 0; i < input_string.length(); i++)
    {
        if (flag == 0 && i + 1 < input_string.length()) {
            if (input_string[i] == '/' && input_string[i + 1] == '/') {
                flag = 1;
                i++;
                continue;
            }
            if (input_string[i] == '/' && input_string[i + 1] == '*') {
                flag = 2;
                i++;
                continue;
            }
        }

        if (flag == 1) {
            if (input_string[i] == '\n') {
                flag = 0;
                output_string += '\n';
            }
            continue;
        }

        if (flag == 2) {
            if (i + 1 < input_string.length() && input_string[i] == '*' && input_string[i + 1] == '/') {
                flag = 0;
                i++;
            }
            continue;
        }

        if (flag == 0) {
            output_string += input_string[i];
        }
    }

    return output_string;
}

