public class CaseChangeTest {
    private static boolean areStringsEqual(String s1, String s2) {
        return s1.equals(s2);
    }

    public static void testChangeCase(String input, String expected) {
        String result = CaseChanger.changeCase(input);
        boolean passed = areStringsEqual(result, expected);
        System.out.println("Test Case for \"" + input + "\": " + (passed ? "Passed" : "Failed") + ", Result: \"" + result + "\", Expected: \"" + expected + "\"");
    }

    public static void main(String[] args) {
        // Test Case 1: Mixed case input
        testChangeCase("Hello World", "hELLO wORLD");

        // Test Case 2: All uppercase
        testChangeCase("JAVA", "java");

        // Test Case 3: All lowercase
        testChangeCase("programming", "PROGRAMMING");

        // Test Case 4: String with numbers and symbols
        testChangeCase("123abcDEF!@#", "123ABCdef!@#");

        // Test Case 5: Empty string
        testChangeCase("", "");

        // Test Case 6: String with only spaces
        testChangeCase("   ", "   ");

        // Test Case 7: Single character
        testChangeCase("A", "a");
        testChangeCase("z", "Z");
    }
}

class CaseChanger {
    public static String changeCase(String input) {
        StringBuilder outputString = new StringBuilder();
        for (int i = 0; i < input.length(); i++) {
            char c = input.charAt(i);
            if (Character.isUpperCase(c)) {
                outputString.append(Character.toLowerCase(c));
            } else if (Character.isLowerCase(c)) { // Added condition for clarity, though else would also catch it
                outputString.append(Character.toUpperCase(c));
            } else {
                outputString.append(c); // Append non-alphabetic characters as-is
            }
        }
        return outputString.toString();
    }
}

