#include <stdio.h>
#include <string.h>
#include <stdlib.h>
#include <unistd.h>  // Added for pipe, dup, close, read, STDOUT_FILENO

// Helper to capture stdout
char* capture_stdout(void (*func)(), int expected_len) {
    fflush(stdout);
    char* buffer = (char*)malloc(sizeof(char) * (expected_len + 1));
    if (buffer == NULL) return NULL;

    // Redirect stdout to a pipe
    int pipefd[2];
    if (pipe(pipefd) == -1) {
        free(buffer);
        return NULL;
    }
    int saved_stdout = dup(STDOUT_FILENO);
    if (saved_stdout == -1) {
        close(pipefd[0]); close(pipefd[1]); free(buffer);
        return NULL;
    }
    if (dup2(pipefd[1], STDOUT_FILENO) == -1) {
        close(pipefd[0]); close(pipefd[1]); close(saved_stdout); free(buffer);
        return NULL;
    }
    close(pipefd[1]); // Close write end in parent

    // Call the function that prints to stdout
    func();

    fflush(stdout);
    if (dup2(saved_stdout, STDOUT_FILENO) == -1) {
        close(pipefd[0]); close(saved_stdout); free(buffer);
        return NULL;
    }
    close(saved_stdout);

    ssize_t bytes_read = read(pipefd[0], buffer, expected_len);
    if (bytes_read == -1) {
        close(pipefd[0]); free(buffer);
        return NULL;
    }
    buffer[bytes_read] = '\0'; // Null-terminate the string
    close(pipefd[0]);

    return buffer;
}

