char* capture_stdout(void (*func)(), size_t* out_len) {
    const char* tmp_file = "temp_stdout.txt";
    FILE* original_stdout = stdout;

    FILE* f = freopen(tmp_file, "w", stdout);
    if (!f) return NULL;

    func();

    fflush(stdout);

    freopen("/dev/tty", "w", stdout);

    FILE* fr = fopen(tmp_file, "r");
    if (!fr) return NULL;

    fseek(fr, 0, SEEK_END);
    long len = ftell(fr);
    rewind(fr);

    char* buffer = (char*)malloc(len + 1);
    if (!buffer) {
        fclose(fr);
        return NULL;
    }
    fread(buffer, 1, len, fr);
    buffer[len] = '\0';

    fclose(fr);
    remove(tmp_file);

    if (out_len) *out_len = len;
    return buffer;
}

