// The max_sum function (as provided in the output)
int max_sum(int arr[], int n)
{
    if (n == 0)
        return 0;

    int max_val = arr[0]; 
    int current_sum = arr[0]; 

    bool all_negative = true;
    for (int i = 0; i < n; ++i) {
        if (arr[i] >= 0) {
            all_negative = false;
            break;
        }
    }
    if (all_negative) {
        return 0;
    }

    for (int i = 1; i < n; i++) { 
        current_sum = std::max(current_sum + arr[i], arr[i]); 
        max_val = std::max(max_val, current_sum); 
    } 

    return max_val; 
}

