import java.text.ParseException;
import java.text.SimpleDateFormat;

public class DateValidator {
    // The method to be tested, directly from the 'output'
    public boolean isValidDate(String date){
        SimpleDateFormat format = new SimpleDateFormat("yyyy-MM-dd");
        try {
            format.parse(date);
            // Set lenient to false to enforce strict date parsing (e.g., 2023-02-30 would be invalid)
            format.setLenient(false);
            format.parse(date);
            return true;
        } catch (ParseException e) {
            return false;
        }
    }

    public static void main(String[] args) {
        DateValidator validator = new DateValidator();

        // Test Case 1: Valid date
        if (validator.isValidDate("2023-01-15")) {
            System.out.println("Test Case 1 (2023-01-15): PASSED");
        } else {
            System.err.println("Test Case 1 (2023-01-15): FAILED");
            System.exit(1);
        }

        // Test Case 2: Invalid month
        if (!validator.isValidDate("2023-13-15")) {
            System.out.println("Test Case 2 (2023-13-15): PASSED");
        } else {
            System.err.println("Test Case 2 (2023-13-15): FAILED");
            System.exit(1);
        }

        // Test Case 3: Invalid day for month
        if (!validator.isValidDate("2023-02-30")) {
            System.out.println("Test Case 3 (2023-02-30): PASSED");
        } else {
            System.err.println("Test Case 3 (2023-02-30): FAILED");
            System.exit(1);
        }

        // Test Case 4: Incorrect format
        if (!validator.isValidDate("15/01/2023")) {
            System.out.println("Test Case 4 (15/01/2023): PASSED");
        } else {
            System.err.println("Test Case 4 (15/01/2023): FAILED");
            System.exit(1);
        }

        // Test Case 5: Empty string
        if (!validator.isValidDate("")) {
            System.out.println("Test Case 5 (empty string): PASSED");
        } else {
            System.err.println("Test Case 5 (empty string): FAILED");
            System.exit(1);
        }

        // Test Case 6: Leap year date
        if (validator.isValidDate("2024-02-29")) { // 2024 is a leap year
            System.out.println("Test Case 6 (2024-02-29): PASSED");
        } else {
            System.err.println("Test Case 6 (2024-02-29): FAILED");
            System.exit(1);
        }

        // Test Case 7: Non-leap year date
        if (!validator.isValidDate("2023-02-29")) { // 2023 is not a leap year
            System.out.println("Test Case 7 (2023-02-29): PASSED");
        } else {
            System.err.println("Test Case 7 (2023-02-29): FAILED");
            System.exit(1);
        }

        System.out.println("All date validation tests passed successfully.");
    }
}

