        for (int i = 0; i <= arr.size() / 2; i++) {
            if (!Objects.equals(arr.get(i), arr.get(arr.size() - i))) {
                ans = ans + 1;
            }
        }
        return ans
    }
}

int ans = 0;
        for (int i = 0; i < arr.size() / 2; i++) {
            if (!Objects.equals(arr.get(i), arr.get(arr.size() - i - 1))) {
                ans += 1;
            }
        }
        return ans;
    }
}

